using System.Text;
using Newtonsoft.Json.Linq;
System.Net.WebClient client = new System.Net.WebClient();
System.Collections.Specialized.NameValueCollection reqparm = new System.Collections.Specialized.NameValueCollection();
Encoding Encoding = new UTF8Encoding(true, true);
    
    // URL da API
    string url = "https://m-formatter.azurewebsites.net/api/v2";
    
    // Parametros de configuração da API passando pelo Body como Json
    reqparm.Add("resultType", "text");
    reqparm.Add("indentationKind", "spaces");
    reqparm.Add("indentationLength", "2");
    reqparm.Add("lineEnd", "\n");
    reqparm.Add("ws", " ");
    reqparm.Add("lineWidth", "100");
    reqparm.Add("numBrackets", "3");
    reqparm.Add("alignPairedLetExpressionsByEqual", "singleline");
    reqparm.Add("alignPairedRecordExpressionsByEqual", "singleline");
    reqparm.Add("alignLineCommentsToPosition", "null");
    reqparm.Add("includeComments", "true");
    // Parametros fixos
    

    //loop nas partições
foreach(var p in Model.AllPartitions.OfType<MPartition>())
{
    // Copia o código M da tabela
    string QueryM = p.Expression;
     
    // Adiciona o código M para enviar API
    reqparm.Add("code", QueryM);
    
    // Envia os dados para a API
    var responsebytes = client.UploadValues(url, "POST", reqparm);

    // resposta da API 
    var responsebody = Encoding.GetString(responsebytes);

   // Parse the response JSON object from the string
   JObject data = JObject.Parse(responsebody.ToString());

   // Get the formatted Power Query response
   string formattedPowerQuery = (string)data["result"];
    
   // Substituir o código M antigo pela versão identada
   p.Expression = formattedPowerQuery;
   
   // remove o parametro Code
   reqparm.Remove("code");
       
}

// Marca a chave primaria em cada tabela
// analisa todos os relacionamentos ativos e se a cardinalidade for 1
// ele marca essa coluna como chave
// E marca as tabelas calendário como tabela de data

foreach (var r in Model.Relationships.ToList())
{
    string fromTable = r.FromTable.Name;
    string fromColumn = r.FromColumn.Name;
    
    string toTable = r.ToTable.Name;
    string toColumn = r.ToColumn.Name;
    
    bool   From = Model.Tables[fromTable].Columns[fromColumn].IsKey;
    bool   To   = Model.Tables[toTable].Columns[toColumn].IsKey;
    
    string Object_From = r.FromColumn.DaxObjectFullName; 
    string Object_To = r.ToColumn.DaxObjectFullName;
    
    bool Excluir_Tabela_From =  !fromTable.StartsWith("LocalDateTable");
    bool Excluir_Tabela_From2 = !fromTable.StartsWith("DateTableTemplate");
    
    bool Excluir_Tabela_To =  !toTable.StartsWith("LocalDateTable");
    bool Excluir_Tabela_To2 = !toTable.StartsWith("DateTableTemplate");
    
    
    if (r.FromCardinality == RelationshipEndCardinality.One && From == false)
    { 
        Model.Tables[fromTable].Columns[fromColumn].IsKey = true;
        
        if (Model.Tables[fromTable].Columns[fromColumn].DataType.ToString() == "DateTime" && !Excluir_Tabela_From && !Excluir_Tabela_From2 )
            {
                //----------As Datas são exclusivas-------------------------
    
                string Count_From =   "COUNT("+Object_From+")";
               
                string Distinct_From =   "DISTINCTCOUNT("+Object_From+")";
                
                string daxResult1_From = EvaluateDax(Count_From + " = " + Distinct_From).ToString(); //se  True as Datas são unicas
                
                //----------As Datas são exclusivas-------------------------
                
                
                //--------As Datas não possuem valores nulos ---------------
                
                string Nulos_From =   "CALCULATE(COUNTROWS("+fromTable+"),"+Object_From+" == BLANK())";
                
                string daxResult2_From = EvaluateDax(Nulos_From + " == BLANK()").ToString();//se  True as Datas não possuem valores nulos
                
                //--------As Datas não possuem valores nulos ---------------
                
                //---------------As Datas são continuas --------------------
                
                string Continuo_From = "(MIN("+Object_From+") + COUNT("+Object_From+") -1) = MAX("+Object_From+")";
                
                string daxResult3_From = EvaluateDax(Continuo_From).ToString(); //se  True as Datas são continuas
                
                //---------------As Datas são continuas --------------------
                
                //---------------Validação de Datas ------------------------
                
                bool Datas_From = daxResult1_From == "True"; //&& daxResult2_From == "True" && daxResult3_From == "True";
                
                //---------------Validação de Datas ------------------------
                
                if (Datas_From)
                {
                Model.Tables[fromTable].DataCategory = "Time";
                }
            }
    }
    
        if (r.ToCardinality == RelationshipEndCardinality.One && To == false)
    {
        Model.Tables[toTable].Columns[toColumn].IsKey = true;
        
        if (Model.Tables[toTable].Columns[toColumn].DataType.ToString() == "DateTime" && Excluir_Tabela_To && Excluir_Tabela_To2)
            {
                //----------As Datas são exclusivas-------------------------
    
                string Count_To =   "COUNT("+Object_To+")";
               
                string Distinct_To =   "DISTINCTCOUNT("+Object_To+")";
                
                string daxResult1_To = EvaluateDax(Count_To + " = " + Distinct_To).ToString(); //se  True as Datas são unicas
                
                //----------As Datas são exclusivas-------------------------
                
                
                //--------As Datas não possuem valores nulos ---------------
                
                string Nulos_To =   "CALCULATE(COUNTROWS("+toTable+"),"+Object_To+" == BLANK()) == BLANK()";
                
                string daxResult2_To = EvaluateDax(Nulos_To).ToString();//se  True as Datas não possuem valores nulos
                
                //--------As Datas não possuem valores nulos ---------------
                
                //---------------As Datas são continuas --------------------
                
                string Continuo_To = "(MIN("+Object_To+") + COUNT("+Object_To+") -1) = MAX("+Object_To+")";
                
                string daxResult3_To = EvaluateDax(Continuo_To).ToString(); //se  True as Datas são continuas
                
