using System.Text.RegularExpressions;

// Regex para pegar idetificar palavras que começam com gold., soliver., bronze_, silver_, db_group_, db_produto_, dbo., bi., transient., reference.
string pattern = @"(?i)(?:gold\.|silver\.|silver_[a-zA-Z_0-9]*\.|bronze_[a-zA-Z_0-9]*\.|db_group_[a-zA-Z_0-9]*\.|db_produto\.[a-zA-Z_0-9]*\.|dbo\.|dw\.|bi\.|transient\.|reference\.)[a-zA-Z_0-9]*";
    List<string> Lista = new List<string>();

    // Para tabelas sem atualização incremental
    foreach(var p in Selected.Tables.SelectMany(c => c.Partitions))
    {
        // Retorna o código M da partição
        string Query_Partition = p.Expression;
        
        // Identifica se a atualização incremental está ativa para a tabela
        bool   Incremental = p.Table.EnableRefreshPolicy;
        string Query = "";
        
        if   // Se incremental true retorna código M da tabela se false retorna da partição
            (!Incremental)
            { 
                Query = Regex.Replace(Query_Partition,"`","");
            }
        
        // Cria uma lista das strings encontradas em cada tabela ou partição
        var    Result = Regex.Matches(Query, pattern).Cast<Match>().ToList();
        if
            // Retorna resultado somente das tabelas que tiveram pelo menos uma string identificada
            (Result.Count() > 0 )
            {   
                //Acumula o resultado na lista
                Lista.AddRange(Result.Select(i => i.Value));  
            }
        }
        
        
    // Para tabelas com atualização incremental
    foreach(var p in Selected.Tables)
    {
        
        // Retorna o código M da Tabela
        string Query_Table = p.SourceExpression;
        
        // Identifica se a atualização incremental está ativa para a tabela
        bool   Incremental = p.EnableRefreshPolicy;
        string Query = "";
        
        if   // Se incremental true retorna código M da tabela se false retorna da partição
            (Incremental)
            {   
                //substitui espaço por vazio
                Query = Regex.Replace(Query_Table,"`","");
            }
        
        // Cria uma lista das strings encontradas em cada tabela ou partição
        var    Result = Regex.Matches(Query, pattern).Cast<Match>().ToList();
        if
            // Retorna resultado somente das tabelas que tiveram pelo menos uma string identificada
            (Result.Count() > 0 )
            {   
                //Acumula o resultado na lista
                Lista.AddRange(Result.Select(i => i.Value));
            }
     }
        
//Retorna o resultado da lista acumulada final
string.Join("\n", Lista.Distinct().Select(i => i)).Output();

