// ------------  Adiciona o Separador de Milhar nas Medidas -------------//

foreach(var c in Model.AllMeasures)
{
    //Salva o formato atual da medida
    string Formato = c.FormatString;
    
    //Salva o tipo atual da medida
    string DataType = c.DataType.ToString();
        
    // Adiciona casa decimal onde as medidas são números
    // e considera somente os formatos abaixo
    if ( DataType == "Double" || DataType == "Decimal" || DataType == "Int64" )
    {
        switch (Formato)
       {
        //Percentual com 2 casas
        case "0.00%;-0.00%;0.00%":
            c.FormatString = "#,0.00%;-#,0.00%;#,0.00%";
            break;

        //Percentual com 1 casa
        case "0.0%;-0.0%;0.0%":
            c.FormatString = "#,0.0%;-#,0.0%;#,0.0%";
            break;
        
        //Percentual com 0 casas
        case "0%;-0%;0%":
            c.FormatString = "#,0%;-#,0%;#,0%";
            break;
            
         //Decimal com 2 casas
        case "0.00":
            c.FormatString = "#,0.00";
            break;
            
        //Decimal com 1 casa
        case "0.0":
            c.FormatString = "#,0.0";
            break;
            
        //Inteiro
        case "0":
            c.FormatString = "#,0";
            break;
                 
        };
        
    }
    
}
//-----------------------------------------------------------------------//

// ------------  Adiciona o Separador de Milhar nas Colunas-------------//
foreach(var c in Model.Tables.SelectMany(c => c.Columns)) 
{
    //Salva o formato atual da medida
    string Data_Type = c.DataType.ToString();
    string Format = c.FormatString;
    

    switch (Data_Type)
   {
    //Data Type quando é double = Decimal
    case "Double":
        switch (Format)
        {   
            // 1 casa decimal
            case "0.0":
            c.FormatString = "#,0.0";
            break;
            
            // 2 casa decimal
            case "0.00":
            c.FormatString = "#,0.00";
            break;
            
            // 3 casa decimal
            case "0.000":
            c.FormatString = "#,0.000";
            break;
            
            
            default:
            c.FormatString = "#,0.############";
            break;
        }
        break;

    //Data Type quando é Int64 = Número Inteiro
    case "Int64":
        c.FormatString = "#,0";
        break;
    
    //Data Type quando é Decimal = Decimal Fixo, ou seja, contém até 4 casas decimais
    case "Decimal":
    c.FormatString = "#,0.####";
        break;
        
    }
        
}
