using System.Data;

// Cria tabela 
var dt = new DataTable();

// Adiciona a coluna Tabela
dt.Columns.Add("Tabela", typeof(string));

// Adiciona a coluna Qtd Linhas TXT
dt.Columns.Add("Qtd Linhas TXT", typeof(string));

// Adiciona a coluna Qtd Linhas
dt.Columns.Add("Qtd Linhas", typeof(int));

// itera todas as tabelas do modelo
foreach(var table in Model.Tables)
    {    
        // verifica se a tabela possui colunas
        if ( table.Columns.Count() > 0 && !(table.SourceType.ToString() == "Calculated") )
        {    
            // nome da tabela
            string Tabela = table.Name;
            
            // Query DAX
            string dax = @"{COUNTROWS('" + Tabela + @"')}";
            
            // Resultado da Query DAX
            string daxResult = EvaluateDax(dax).ToString();
            
            // Resultado da Query DAX
            string daxResultTXT = EvaluateDax(@"{FORMAT(" + dax + @",""#,0"")}").ToString();
            
            // Adiciona o resultado na tabela
            dt.Rows.Add(Tabela, daxResultTXT, daxResult);
        }
}
    // Ordena a tabela pela quantidade de linhas
    dt.DefaultView.Sort = "Qtd Linhas desc";
    
    // Mostra o resultado
    dt.Output();
